mapper.select:
SELECT DISTINCT p.post_id,p.post_finished,p.comment_count, p.post_status, p.post_type, p.post_name, p.post_title,p.post_content,p.post_date,p.post_modified, su.user_name,su.nick_name,su.user_profile,su.user_url,su.mobile,su.email,su.status,su.user_desc,su.user_registered, sr.id AS role_id,sr.name AS role_name,sr.cn_name
,CAST(if(pm.meta_key='view_count',pm.meta_value,0) AS unsigned)
FROM bg_posts p LEFT JOIN bg_postmeta pm ON pm.post_id = p.post_id LEFT JOIN sys_user su ON su.id = p.post_author LEFT JOIN sys_role_user sru on su.id= sru.sys_user_id LEFT JOIN sys_role sr on sru.sys_role_id = sr.id WHERE 1=1
AND p.post_status = #{postStatus}
AND p.post_type = #{postType}
AND p.post_author = #{userId}
AND pm.meta_key = #{metaKey}
AND pm.meta_value = #{metaValue}
AND ((p.post_title LIKE concat(concat('%',#{search}),'%')) OR (p.post_content LIKE concat(concat('%',#{search}),'%')))
ORDER BY CAST(if(pm.meta_key='view_count',pm.meta_value,0) AS unsigned) DESC
ORDER BY p.post_modified DESC

SELECT pm.meta_key AS metaKey,ifnull(pm.meta_value, '文章') AS metaValue
FROM bg_postmeta pm
WHERE pm.post_id = #{post_id}
